import React from 'react';

import GridProto from "material-ui/Grid";

export default class Grid extends GridProto {


  render() {

    const {
      spacing,
      style,
      ...other
    } = this.props;

    let content = super.render();


    return spacing > 0 ? <div
      style={{
        padding: spacing / 2,
        width: "100%",
        ...style,
      }}
    >{content}</div> : content;

  }

}